import React, { useEffect, useState } from "react";
import { useParams,useNavigate  } from "react-router-dom";

type Instalment = {
  instalment_number: number;
  due_date: string;
  amount: number;
  status: "PAID" | "UPCOMING" | "FAILED" | "DUE";
};

type Agreement = {
  agreement_id: string;
  user_id: string;
  cart_id: string;
  total_amount: number;
  currency: string;
  schedule: Instalment[];
  status: string;
};

type ActivityLogEntry = {
  agreement_id: string;
  timestamp: string;
  action: string;
  detail?: string;
};

export default function PayLaterInstallmentList() {
  const { agreementId } = useParams();
  const [agreement, setAgreement] = useState<Agreement | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [logs, setLogs] = useState<ActivityLogEntry[]>([]);
    const navigate = useNavigate();


  const fetchAgreement = async () => {
    if (!agreementId) return;
    setLoading(true);
    try {
      // fetch agreement
      const res = await fetch(`http://localhost:3000/api/paylater/agreement/${agreementId}`);
      const data: Agreement = await res.json();
      setAgreement(data);

      // fetch logs
      const logRes = await fetch(`http://localhost:3000/api/paylater/activity-log?agreementId=${agreementId}`);
      const logData: ActivityLogEntry[] = await logRes.json();
      setLogs(logData);
    } catch (err) {
      console.error(err);
      setError("Failed to load agreement or logs");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAgreement();
  }, [agreementId]);

  const handleFail = async (instalmentNumber: number) => {
    if (!agreement) return;
    try {
      await fetch(`http://localhost:3000/api/paylater/agreement/${agreement.agreement_id}/fail`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ instalmentNumber }),
      });
      fetchAgreement();
    } catch (err) {
      console.error(err);
    }
  };

  const handleRetry = async (instalmentNumber: number) => {
    if (!agreement) return;
    try {
      await fetch(`http://localhost:3000/api/paylater/agreement/${agreement.agreement_id}/retry`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ instalmentNumber }),
      });
      fetchAgreement();
    } catch (err) {
      console.error(err);
    }
  };

  if (loading) return <div style={{ marginTop: 80, textAlign: "center" }}>Loading...</div>;
  if (!agreement) return <div style={{ marginTop: 80, textAlign: "center" }}>No agreement found</div>;

  return (
    <div style={{ padding: 32, minHeight: "100vh", background: "#F6FCFA" }}>
      <div style={{ maxWidth: 480, margin: "0 auto", background: "#fff", borderRadius: 20, padding: 24, boxShadow: "0 2px 12px #38C87B22" }}>
        <h2 style={{ color: "#13444E", marginBottom: 18 }}>Payment Schedule Tracking</h2>
        {agreement.schedule.map(inst => (
          <div key={inst.instalment_number} style={{ display: "flex", justifyContent: "space-between", alignItems: "center", padding: 12, borderBottom: "1px solid #E0E0E0" }}>
            <div>
              <div style={{ fontWeight: 600 }}>Instalment {inst.instalment_number}</div>
              <div style={{ fontSize: 14, color: "#4B655C" }}>Due: {new Date(inst.due_date).toLocaleDateString()}</div>
              <div style={{ fontSize: 14, color: "#4B655C" }}>Amount: ${inst.amount.toFixed(2)}</div>
            </div>
            <div style={{ display: "flex", gap: 8, alignItems: "center" }}>
              <span style={{
                fontWeight: 700,
                color:
                  inst.status === "PAID"
                    ? "#38C87B"
                    : inst.status === "FAILED"
                    ? "#D32F2F"
                    : inst.status === "DUE"
                    ? "#FF6F00"
                    : "#FFA000"
              }}>
                {inst.status}
              </span>
              {["UPCOMING", "DUE"].includes(inst.status) && (
                <button
                  onClick={() => handleFail(inst.instalment_number)}
                  style={{
                    padding: "4px 8px",
                    background: "#D32F2F",
                    color: "#fff",
                    border: "none",
                    borderRadius: 4,
                    cursor: "pointer"
                  }}
                >
                  Fail
                </button>
              )}
              {["FAILED", "DUE"].includes(inst.status) && (
                <button
                  onClick={() => handleRetry(inst.instalment_number)}
                  style={{
                    padding: "4px 8px",
                    background: "#1976D2",
                    color: "#fff",
                    border: "none",
                    borderRadius: 4,
                    cursor: "pointer"
                  }}
                >
                  Retry
                </button>
              )}
            </div>
          </div>
        ))}
      </div>

      {logs.length > 0 && (
        <div style={{ marginTop: 24, fontSize: 12, color: "#555" }}>
          <h4>Activity Log (Developer)</h4>
          {logs.map((log, idx) => (
            <div key={idx}>
              [{new Date(log.timestamp).toLocaleString()}] {log.action} {log.detail ? `- ${log.detail}` : ""}
            </div>
          ))}
        </div>
      )}
       <button
        onClick={() => navigate("/dev/scenarios")}
        style={{ marginTop: 24, padding: "8px 16px", background: "#1976D2", color: "#fff", border: "none", borderRadius: 4 }}
      >
        Open Dev/Test Scenario Dashboard
      </button>

      {error && <div style={{ textAlign: "center", color: "red", marginTop: 12 }}>{error}</div>}
    </div>
  );
}
