import React, { useState, useEffect } from "react";

type User = {
  user_id: string;
  name: string;
  timezone: string;
  locale: string;
};

type UserPickerProps = {
  users: User[];
  onSelect: (user_id: string, index: number) => void;
};

const BRAND_COLORS = ["#38C87B", "#62BE76", "#1C9085", "#13444E", "#B7E82A"];
const getAvatarColor = (i: number) => BRAND_COLORS[i % BRAND_COLORS.length];

export const UserPicker: React.FC<UserPickerProps> = ({ users, onSelect }) => {
  const [current, setCurrent] = useState(0);

  // Load stored index when return back
  useEffect(() => {
    const savedIndex = localStorage.getItem("selected_user_index");
    if (savedIndex) {
      const idx = Number(savedIndex);
      if (idx >= 0 && idx < users.length) setCurrent(idx);
    }
  }, [users.length]);

  const next = () => setCurrent((c) => Math.min(users.length - 1, c + 1));
  const prev = () => setCurrent((c) => Math.max(0, c - 1));

  return (
    <div style={{ marginTop: 20 }}>

      {/* Carousel + arrows */}
      <div
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          gap: "6vw",
          flexWrap: "wrap",
        }}
      >
        {/* LEFT ARROW */}
        <button
          onClick={prev}
          disabled={current === 0}
          style={arrowStyle(current === 0)}
        >
          ←
        </button>

        {/* Center card */}
        <UserCard
          user={users[current]}
          idx={current}
          selected
          onSelect={() => onSelect(users[current].user_id, current)}
        />

        {/* RIGHT ARROW */}
        <button
          onClick={next}
          disabled={current === users.length - 1}
          style={arrowStyle(current === users.length - 1)}
        >
          →
        </button>
      </div>

      {/* Indicators */}
      <div style={{ display: "flex", justifyContent: "center", marginTop: 18, gap: 10 }}>
        {users.map((_u, i) => (
          <div
            key={i}
            style={{
              width: 12,
              height: 12,
              borderRadius: "50%",
              background: i === current ? "#B7E82A" : "#62BE76",
              opacity: i === current ? 1 : 0.3,
              transition: "0.25s",
              boxShadow: i === current ? "0 0 8px #B7E82Aaa" : "none",
            }}
          />
        ))}
      </div>

    </div>
  );
};


// Reusable arrow style
const arrowStyle = (disabled: boolean): React.CSSProperties => ({
  background: disabled ? "#ffffff44" : "#fff",
  border: "none",
  borderRadius: "50%",
  width: 50,
  height: 50,
  cursor: disabled ? "not-allowed" : "pointer",
  boxShadow: "0 2px 10px #13444E55",
  fontSize: 26,
  fontWeight: 700,
  color: disabled ? "#13444E55" : "#1C9085",
  transition: "0.25s",
});


// User card
function UserCard({
  user,
  idx,
  selected,
  onSelect,
}: {
  user: User;
  idx: number;
  selected: boolean;
  onSelect?: () => void;
}) {
  return (
    <div
      style={{
        width: "80vw",
        maxWidth: 300,
        background: "#fff",
        borderRadius: 22,
        padding: "26px 20px",
        boxShadow: selected ? "0 8px 32px #38C87B44" : "0 2px 12px #13444E22",
        border: selected ? "3px solid #38C87B" : "2px solid #62BE7622",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        transition: "0.25s",
      }}
    >
      {/* Avatar */}
      <div
        style={{
          background: getAvatarColor(idx),
          width: 92,
          height: 92,
          borderRadius: "50%",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          color: "#fff",
          fontSize: 34,
          fontWeight: 700,
          marginBottom: 18,
          boxShadow: "0 4px 14px #1C908555",
        }}
      >
        {user.name
          .split(" ")
          .map((x) => x[0])
          .join("")
          .toUpperCase()}
      </div>

      {/* Name */}
      <div style={{ fontWeight: 700, color: "#13444E", fontSize: 22, marginBottom: 6 }}>
        {user.name}
      </div>

      <div style={{ fontSize: 15, color: "#1C9085", marginBottom: 4 }}>{user.timezone}</div>
      <div style={{ fontSize: 15, color: "#62BE76", marginBottom: 22 }}>{user.locale}</div>

      <button
        onClick={onSelect}
        style={{
          background: "#38C87B",
          color: "#fff",
          width: "100%",
          padding: "12px 0",
          border: "none",
          borderRadius: 12,
          fontSize: 16,
          fontWeight: 600,
          cursor: "pointer",
          boxShadow: "0 2px 10px #38C87B55",
          transition: "0.2s",
        }}
      >
        Select
      </button>
    </div>
  );
}
