import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";




type Cart = {
  cart_id: string;
  user_id: string;
  total_amount: number;
  eligible_threshold: number;
  item_count: number;
};

type User = {
  user_id: string;
  name: string;
  timezone: string;
  locale: string;
};

export default function PaymentOptionsScreen() {
  const { user_id, cart_id } = useParams();
  const navigate = useNavigate();

  const [user, setUser] = useState<User | null>(null);
  const [carts, setCarts] = useState<Cart[]>([]);
  const [selectedCartIdx, setSelectedCartIdx] = useState<number>(0);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch(`http://localhost:3000/api/paylater/users`)
      .then(res => res.json())
      .then(users => {
        const foundUser = users.find((u: User) => u.user_id === user_id);
        setUser(foundUser || null);
      })
      .catch(err => console.error(err));
  }, [user_id]);

  useEffect(() => {
    fetch(`http://localhost:3000/api/paylater/users/${user_id}/carts`)
      .then(res => res.json())
      .then(data => {
        setCarts(data);
        if (cart_id) {
          const idx = data.findIndex((c: Cart) => c.cart_id === cart_id);
          setSelectedCartIdx(idx >= 0 ? idx : 0);
        }
      })
      .catch(err => console.error(err))
      .finally(() => setLoading(false));
  }, [user_id, cart_id]);

  if (loading || !user || !carts.length) {
    return <div style={{ color: "#38C87B", textAlign: "center", marginTop: 80 }}>Loading...</div>;
  }

  const cart = carts[selectedCartIdx];
  const canContinue = cart.total_amount >= cart.eligible_threshold;
  const isMobile = window.innerWidth <= 650;

  const handleSelectCart = (idx: number) => {
    setSelectedCartIdx(idx);
  };

const handleContinue = () => {
  navigate(`/user/${user.user_id}/cart/${cart.cart_id}/paylater-detail`);
};


return (
  <div style={{
    background: "linear-gradient(180deg,#F6FCFA 60%,#E8F5F0 100%)",
    minHeight: "100vh",
    padding: isMobile ? "0 0 18px 0" : "38px 0",
    display: "flex",
    justifyContent: "center",
    alignItems: "flex-start"
  }}>
    <div style={{
      background: "#fff",
      borderRadius: 24,
      boxShadow: "0 6px 40px #13444e1a",
      maxWidth: isMobile ? 375 : 440,
      width: "100%",
      marginTop: isMobile ? 0 : 40,
      padding: isMobile ? "0" : "40px 0"
    }}>
      {/* Header */}
      <div style={{
        display: "flex",
        alignItems: "center",
        padding: "18px 18px 4px 12px",
        background: "#fff",
        borderRadius: "24px 24px 0 0",
        boxShadow: "0 1px 8px #13444e0a"
      }}>
        <button
          onClick={() => navigate(-1)}
          style={{
            background: "transparent",          // NO background
            border: "none",
            borderRadius: "50%",
            width: 39,
            height: 39,
            color: "#13444E",
            fontSize: 23,
            fontWeight: 700,
            cursor: "pointer",
            marginRight: 12,
            outline: "none",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            transition: "color 0.16s"
          }}
          aria-label="Back"
        >‚Üê</button>
        <div style={{
          flex: 1,
          fontSize: 19,
          color: "#13444E",
          fontWeight: 700,
          textAlign: "center",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          gap: 10
        }}>
          <img
            src="/shopping-cart.png"
            alt="Cart"
            style={{ width: 24, height: 24, marginRight: 7, objectFit: "contain" }}
          />
          Cart
        </div>
        <div style={{ width: 39 }} />
      </div>

      {/* Multiple carts (if any) */}
      {carts.length > 1 && (
        <div style={{
          display: "flex",
          gap: 10,
          padding: "13px 16px 10px 16px",
          background: "#F6FCFA",
          overflowX: "auto",
          borderBottom: "1px solid #EBEBEB"
        }}>
          {carts.map((c, idx) => (
            <button
              key={c.cart_id}
              onClick={() => handleSelectCart(idx)}
              style={{
                padding: "10px 20px",
                borderRadius: 12,
                border: idx === selectedCartIdx ? "2px solid #38C87B" : "1px solid #EBEBEB",
                background: idx === selectedCartIdx ? "#E8F5F0" : "#fff",
                color: idx === selectedCartIdx ? "#38C87B" : "#13444E",
                fontWeight: idx === selectedCartIdx ? 700 : 500,
                fontSize: 14.8,
                cursor: "pointer",
                boxShadow: idx === selectedCartIdx ? "0 0 7px #38C87B13" : "none",
                transition: "box-shadow 0.14s"
              }}
            >${c.total_amount}
            </button>
          ))}
        </div>
      )}

      {/* Cart summary */}
      <div style={{
        background: "linear-gradient(90deg,#F6FCFA 70%,#E8F5F0 100%)",
        borderRadius: 20,
        margin: "24px 16px 12px 16px",
        padding: "20px 32px",
        textAlign: "center",
        boxShadow: "0 1px 6px #38c87b0a"
      }}>
        <div style={{ fontSize: 15, color: "#62BE76", marginBottom: 10 }}>
          <span role="img" aria-label="money" style={{ fontSize: 18, marginRight: 4 }}>üí∞</span>
          Total Amount
        </div>
        <div style={{
          fontSize: 34,
          color: "#38C87B",
          fontWeight: 700,
          letterSpacing: 0.4,
          textShadow: "#EEF5F2 0px 2px 13px"
        }}>
          ${cart.total_amount}
          <span style={{
            fontSize: 17.5,
            color: "#13444E",
            fontWeight: 500,
            marginLeft: 4
          }}>USD</span>
        </div>
        <div style={{
          marginTop: 18,
          fontSize: 15.6,
          color: "#13444E",
          fontWeight: 600
        }}>
          {cart.item_count} items
        </div>
        {!canContinue && (
          <div style={{
            marginTop: 13,
            color: "#B7E82A",
            fontSize: 13,
            fontStyle: "italic"
          }}>
            PayLater available from ${cart.eligible_threshold.toFixed(2)}
          </div>
        )}
      </div>

      {/* Continue button */}
      <div style={{ margin: "30px 16px 0 16px" }}>
        <button
          disabled={!canContinue}
          style={{
            background: canContinue ? "linear-gradient(90deg,#62BE76,#38C87B)" : "#C2E8C8",
            color: "#fff",
            border: "none",
            borderRadius: 22,
            width: "100%",
            padding: "18px 0",
            fontWeight: 700,
            fontSize: 21,
            cursor: canContinue ? "pointer" : "not-allowed",
            boxShadow: canContinue ? "0 2.5px 14px #62BE7690" : "none",
            transition: "background 0.15s,box-shadow 0.15s"
          }}
            onClick={handleContinue}
          >
            Continue
          </button>
        </div>
      </div>
    </div>
  );
}
