import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";




type Cart = {
  cart_id: string;
  user_id: string;
  total_amount: number;
  eligible_threshold: number;
  item_count: number;
};

type User = {
  user_id: string;
  name: string;
  timezone: string;
  locale: string;
};

export default function PaymentOptionsScreen() {
  const { user_id, cart_id } = useParams();
  const navigate = useNavigate();

  const [user, setUser] = useState<User | null>(null);
  const [carts, setCarts] = useState<Cart[]>([]);
  const [selectedCartIdx, setSelectedCartIdx] = useState<number>(0);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch(`http://localhost:3000/api/paylater/users`)
      .then(res => res.json())
      .then(users => {
        const foundUser = users.find((u: User) => u.user_id === user_id);
        setUser(foundUser || null);
      })
      .catch(err => console.error(err));
  }, [user_id]);

  useEffect(() => {
    fetch(`http://localhost:3000/api/paylater/users/${user_id}/carts`)
      .then(res => res.json())
      .then(data => {
        setCarts(data);
        if (cart_id) {
          const idx = data.findIndex((c: Cart) => c.cart_id === cart_id);
          setSelectedCartIdx(idx >= 0 ? idx : 0);
        }
      })
      .catch(err => console.error(err))
      .finally(() => setLoading(false));
  }, [user_id, cart_id]);

  if (loading || !user || !carts.length) {
    return <div style={{ color: "#38C87B", textAlign: "center", marginTop: 80 }}>Loading...</div>;
  }

  const cart = carts[selectedCartIdx];
  const canContinue = cart.total_amount >= cart.eligible_threshold;
  const isMobile = window.innerWidth <= 650;

  const handleSelectCart = (idx: number) => {
    setSelectedCartIdx(idx);
  };

const handleContinue = () => {
  navigate(`/user/${user.user_id}/cart/${cart.cart_id}/paylater-detail`);
};


return (
  <div style={{
    background: "#F9FAFB",
    minHeight: "100vh",
    padding: isMobile ? "0 0 14px 0" : "36px 0",
    display: "flex",
    justifyContent: "center",
    alignItems: "flex-start"
  }}>
    <div style={{
      background: "#fff",
      borderRadius: 22,
      boxShadow: "0 8px 46px #13444e16",
      maxWidth: isMobile ? 375 : 420,
      width: "100%",
      marginTop: isMobile ? 0 : 30,
      padding: isMobile ? "0" : "35px 0"
    }}>
      {/* Header */}
      <div style={{
        display: "flex",
        alignItems: "center",
        padding: "18px 18px 4px 10px",
        background: "#fff",
        borderRadius: "22px 22px 0 0",
        boxShadow: "0 1.5px 10px #13444e09"
      }}>
        <button
          onClick={() => navigate(-1)}
          style={{
            background: "transparent",
            border: "none",
            borderRadius: "50%",
            width: 40,
            height: 40,
            color: "#13444E",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            marginRight: 13,
            padding: 0,
            cursor: "pointer"
          }}
          aria-label="Back"
        >
          {/* iOS Back Icon SVG */}
          <svg width="24" height="24" fill="none" stroke="#13444E" strokeWidth="3" strokeLinecap="round" strokeLinejoin="round" viewBox="0 0 24 24" style={{ display: "block" }}>
            <path d="M15 18l-6-6 6-6"></path>
          </svg>
        </button>
        <div style={{
          flex: 1,
          fontSize: 19,
          color: "#13444E",
          fontWeight: 700,
          textAlign: "center",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          gap: 9
        }}>
          <img src="/shopping-cart.png" alt="Cart" style={{ width: 22, height: 22, marginRight: 7, objectFit: "contain" }} />
          Cart
        </div>
        <div style={{ width: 40 }} />
      </div>

      {/* Multiple carts (if any) â€“ style tá»«ng má»¥c nhÆ° card mini */}
      {carts.length > 1 && (
        <div style={{
          display: "flex",
          gap: 12,
          padding: "13px 16px 10px 16px",
          background: "#F6FCFA",
          borderBottom: "1px solid #ECF1EE",
          overflowX: "auto"
        }}>
          {carts.map((c, idx) => (
            <div
              key={c.cart_id}
              onClick={() => handleSelectCart(idx)}
              style={{
                minWidth: 92,
                background: idx === selectedCartIdx ? "#E8F5F0" : "#fff",
                border: idx === selectedCartIdx ? "2.2px solid #38C87B" : "1.1px solid #EBEBEB",
                borderRadius: 13,
                boxShadow: idx === selectedCartIdx ? "0 4px 8px #38C87B18" : "0 1.5px 8px #13444e08",
                color: idx === selectedCartIdx ? "#38C87B" : "#13444E",
                fontWeight: idx === selectedCartIdx ? 700 : 500,
                fontSize: 15,
                textAlign: "center",
                cursor: "pointer",
                padding: "14px 0",
                transition: "0.17s",
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
                justifyContent: "center"
              }}
            >
              <span style={{ fontWeight: 700, fontSize: 17 }}>${c.total_amount}</span>
              <span style={{ color: "#B1B7BA", fontSize: 13, fontWeight: 500 }}>
                {c.item_count} items
              </span>
            </div>
          ))}
        </div>
      )}

      {/* Cart summary */}
      <div style={{
        background: "#FAFAFB",
        borderRadius: 18,
        margin: "25px 15px 12px 15px",
        padding: "18px 27px",
        textAlign: "center",
        boxShadow: "0 0.5px 7px #38c87b0a"
      }}>
        <div style={{ fontSize: 15, color: "#62BE76", marginBottom: 10 }}>
          <span role="img" aria-label="money" style={{ fontSize: 18, marginRight: 4 }}>ðŸ’°</span>
          Total Amount
        </div>
        <div style={{
          fontSize: 32,
          color: "#38C87B",
          fontWeight: 700,
          letterSpacing: 0.3,
          textShadow: "#EEF5F2 0px 1.5px 9px"
        }}>
          ${cart.total_amount}
          <span style={{
            fontSize: 17,
            color: "#13444E",
            fontWeight: 400,
            marginLeft: 4
          }}>USD</span>
        </div>
        <div style={{
          marginTop: 14,
          fontSize: 15,
          color: "#13444E",
          fontWeight: 600
        }}>
          {cart.item_count} items
        </div>
        {!canContinue && (
          <div style={{
            marginTop: 10,
            color: "#B7E82A",
            fontSize: 12.8,
            fontStyle: "italic"
          }}>
            PayLater available from ${cart.eligible_threshold.toFixed(2)}
          </div>
        )}
      </div>

      {/* Continue button */}
      <div style={{ margin: "27px 14px 0 14px" }}>
        <button
          disabled={!canContinue}
          style={{
            background: canContinue ? "linear-gradient(90deg,#62BE76,#38C87B)" : "#C2E8C8",
            color: "#fff",
            border: "none",
            borderRadius: 19,
            width: "100%",
            padding: "17px 0",
            fontWeight: 700,
            fontSize: 20,
            cursor: canContinue ? "pointer" : "not-allowed",
            boxShadow: canContinue ? "0 2.5px 14px #62be7690" : "none",
            transition: "background 0.16s, box-shadow 0.13s"
          }}
          onClick={handleContinue}
        >
          Continue
        </button>
      </div>
    </div>
  </div>
);
}
