import React, { useEffect, useState } from "react";
import { useParams,useNavigate  } from "react-router-dom";

type Instalment = {
  instalment_number: number;
  due_date: string;
  amount: number;
  status: "PAID" | "UPCOMING" | "FAILED" | "DUE";
};

type Agreement = {
  agreement_id: string;
  user_id: string;
  cart_id: string;
  total_amount: number;
  currency: string;
  schedule: Instalment[];
  status: string;
};

type ActivityLogEntry = {
  agreement_id: string;
  timestamp: string;
  action: string;
  detail?: string;
};

export default function PayLaterInstallmentList() {
  const { agreementId } = useParams();
  const [agreement, setAgreement] = useState<Agreement | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [logs, setLogs] = useState<ActivityLogEntry[]>([]);
    const navigate = useNavigate();


  const fetchAgreement = async () => {
    if (!agreementId) return;
    setLoading(true);
    try {
      // fetch agreement
      const res = await fetch(`http://localhost:3000/api/paylater/agreement/${agreementId}`);
      const data: Agreement = await res.json();
      setAgreement(data);

      // fetch logs
      const logRes = await fetch(`http://localhost:3000/api/paylater/activity-log?agreementId=${agreementId}`);
      const logData: ActivityLogEntry[] = await logRes.json();
      setLogs(logData);
    } catch (err) {
      console.error(err);
      setError("Failed to load agreement or logs");
    } finally {
      setLoading(false);
    }
  };
function formatDate(str: string) {
  const d = new Date(str);
  if (isNaN(d.getTime())) return "--";
  return d.toLocaleDateString("en-GB", { year: "numeric", month: "2-digit", day: "2-digit" });
}
function calcRemaining(schedule) {
  return schedule.filter(i => i.status !== "PAID").reduce((sum, i) => sum + i.amount, 0);
}
  useEffect(() => {
    fetchAgreement();
  }, [agreementId]);

  const handleFail = async (instalmentNumber: number) => {
    if (!agreement) return;
    try {
      await fetch(`http://localhost:3000/api/paylater/agreement/${agreement.agreement_id}/fail`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ instalmentNumber }),
      });
      fetchAgreement();
    } catch (err) {
      console.error(err);
    }
  };

  const handleRetry = async (instalmentNumber: number) => {
    if (!agreement) return;
    try {
      await fetch(`http://localhost:3000/api/paylater/agreement/${agreement.agreement_id}/retry`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ instalmentNumber }),
      });
      fetchAgreement();
    } catch (err) {
      console.error(err);
    }
  };

  if (loading) return <div style={{ marginTop: 80, textAlign: "center" }}>Loading...</div>;
  if (!agreement) return <div style={{ marginTop: 80, textAlign: "center" }}>No agreement found</div>;

  return (
     <div style={{
    background: "#F6FCFA",
    minHeight: "100vh",
    padding: 34,
    display: "flex",
    justifyContent: "center"
  }}>
    <div style={{
      background: "#fff",
      borderRadius: 18,
      boxShadow: "0 4px 24px #13444E15",
      maxWidth: 400,
      width: "100%",
      margin: "0 auto",
      padding: "28px 22px 22px 22px"
    }}>

      {/* Header Plan info */}
      <div style={{
        borderBottom: "1px solid #EFF0F3",
        marginBottom: 19,
        paddingBottom: 18
      }}>
        <div style={{
          color: "#13444E",
          fontWeight: 700,
          fontSize: 16,
          letterSpacing: 0.6,
          marginBottom: 11
        }}>
          PayLater Plan ID <span style={{ fontSize: 13, color: "#66707A", marginLeft: 7, fontWeight: 500 }}>
            #{agreement.agreement_id?.slice(0, 10).toUpperCase()}
          </span>
        </div>
        <div style={{
          display: "flex",
          justifyContent: "space-between",
          fontSize: 14,
          color: "#91979C",
          marginBottom: 5
        }}>
          <span>TRANSACTION DATE</span>
          <span style={{
            color: "#13444E",
            fontWeight: 700,
            fontSize: 14
          }}>
            {agreement.created_at ? formatDate(agreement.created_at) : "--"}
          </span>
        </div>
        <div style={{
          display: "flex",
          justifyContent: "space-between",
          fontSize: 15,
          fontWeight: 700,
          marginBottom: 2
        }}>
          <span>TOTAL AMOUNT</span>
          <span style={{ color: "#38C87B", fontWeight: 800 }}>
            ${agreement.total_amount.toFixed(2)}
          </span>
        </div>
        <div style={{
          display: "flex",
          justifyContent: "space-between",
          fontSize: 14,
          marginBottom: 2
        }}>
          <span>REMAINING BALANCE</span>
          <span style={{
            color: "#146B5B",
            fontWeight: 700
          }}>
            ${calcRemaining(agreement.schedule).toFixed(2)}
          </span>
        </div>
        <div style={{
          display: "flex",
          justifyContent: "space-between",
          fontSize: 14,
          color: "#807E80"
        }}>
          <span>LATE FEE INFO:</span>
          <span style={{
            fontWeight: 600,
            color: "#E7A12A"
          }}>
            ${lateFee ?? "0"}
          </span>
        </div>
      </div>

      {/* Timeline */}
      <div style={{ marginBottom: 10 }}>
        {agreement.schedule.map((inst, idx) => {
          // Status styling
          let circle, color, statusLabel;
          if (inst.status === "PAID") {
            circle = <span style={{
              width: 20, height: 20, borderRadius: "50%", background: "#38C87B",
              color: "#fff", display: "flex", alignItems: "center", justifyContent: "center", fontSize: 13, marginRight: 8
            }}>✓</span>;
            color = "#38C87B";
            statusLabel = "PAID";
          } else if (inst.status === "FAILED") {
            circle = <span style={{
              width: 20, height: 20, borderRadius: "50%", background: "#D32F2F",
              color: "#fff", display: "flex", alignItems: "center", justifyContent: "center", fontSize: 14, marginRight: 8
            }}>×</span>;
            color = "#D32F2F";
            statusLabel = "FAILED";
          } else { // UPCOMING, DUE,...
            circle = <span style={{
              width: 20, height: 20, borderRadius: "50%", border: "2px solid #E0A612",
              color: "#E0A612", display: "flex", alignItems: "center", justifyContent: "center", fontWeight: 700, fontSize: 13, background: "#F8F6EF", marginRight: 8
            }}>{idx + 1}</span>;
            color = "#E0A612";
            statusLabel = inst.status;
          }
          return (
            <div key={inst.instalment_number} style={{
              display: "flex", alignItems: "center", marginBottom: 11, position: "relative", minHeight: 55
            }}>
              <div style={{ marginLeft: 1 }}>
                {circle}
              </div>
              {/* Dotted line (timeline) */}
              {idx < agreement.schedule.length - 1 && (
                <div style={{
                  position: "absolute", left: 10, top: 23,
                  height: 38, width: 0, borderLeft: "2px dotted #EDE8CF"
                }} />
              )}
              <div style={{
                flex: 1, marginLeft: 7, boxShadow: "0 1.5px 6px #EDE3BF11", borderRadius: 10,
                background: "#F8F7F3", padding: "9px 15px", display: "flex", flexDirection: "column"
              }}>
                <div style={{
                  fontSize: 13.5,
                  color: color,
                  fontWeight: inst.status === "PAID" ? 600 : 700,
                  letterSpacing: 0.1,
                  marginBottom: 1
                }}>
                  {`${inst.instalment_number}/${agreement.schedule.length} : ${formatDate(inst.due_date)} `}
                  <span style={{ textTransform: "uppercase", fontWeight: 700 }}>({statusLabel})</span>
                </div>
                <div style={{
                  background: "#fff",
                  border: "1px solid #F3F3F3",
                  borderRadius: 7,
                  fontWeight: 700,
                  fontSize: 14.2,
                  color: "#3A4D3D",
                  padding: "7px 12px",
                  marginTop: 4,
                  boxShadow: "0 0.5px 3px #dde8d9"
                }}>
                  Amount Due : ${inst.amount.toFixed(2)}
                </div>
              </div>
            </div>
          );
        })}
      </div>

      {/* CLOSE button center */}
      <div style={{ display: "flex", justifyContent: "center", marginTop: 20 }}>
        <button
          onClick={() => navigate(-1)}
          style={{
            background: "#fafafc",
            color: "#13444E",
            border: "1px solid #C3D6C9",
            borderRadius: 15,
            fontWeight: 600,
            fontSize: 16,
            padding: "8px 30px",
            letterSpacing: 1.1,
            boxShadow: "0 2px 12px #C9D8CF19",
            cursor: "pointer",
            transition: "box-shadow 0.13s",
          }}
        >CLOSE ×</button>
      </div>

      {/* Nếu có lỗi hoặc log dev */}
      {error && <div style={{ color: "#D32F2F", textAlign: "center", marginTop: 10 }}>{error}</div>}
      {logs.length > 0 && (
        <div style={{ marginTop: 14, fontSize: 12, color: "#555" }}>
          <h4>Activity Log (Developer)</h4>
          {logs.map((log, idx) => (
            <div key={idx}>
              [{new Date(log.timestamp).toLocaleString()}] {log.action} {log.detail ? `- ${log.detail}` : ""}
            </div>
          ))}
        </div>
      )}
    </div>
  </div>
);
}
