import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { formatDate } from "../utils/date";
type Cart = {
  cart_id: string;
  total_amount: number;
  eligible_threshold: number;
  item_count: number;
};

type User = {
  user_id: string;
  name: string;
  timezone: string;
  locale: string;
};

type Instalment = {
  due_date: string;
  amount: number;
};

export default function PaylaterDetailScreen() {
  const { user_id, cart_id } = useParams();
  const navigate = useNavigate();

  const [user, setUser] = useState<User | null>(null);
  const [cart, setCart] = useState<Cart | null>(null);
  const [instalments, setInstalments] = useState<Instalment[]>([]);
  const [eligibility, setEligibility] = useState<{ eligible: boolean; reason?: string }>({ eligible: false });
  const [loading, setLoading] = useState(true);
  const [isCreating, setIsCreating] = useState(false);
  const [error, setError] = useState("");

  useEffect(() => {
    async function fetchData() {
      try {
        // Fetch user
        const usersRes = await fetch(`http://localhost:3000/api/paylater/users`);
        const users: User[] = await usersRes.json();
        const foundUser = users.find(u => u.user_id === user_id);
        setUser(foundUser || null);

        // Fetch cart
        const cartsRes = await fetch(`http://localhost:3000/api/paylater/users/${user_id}/carts`);
        const carts: Cart[] = await cartsRes.json();
        const foundCart = carts.find(c => c.cart_id === cart_id);
        setCart(foundCart || null);

        if (foundUser && foundCart) {
          // Check eligibility via API
          const res = await fetch(
            `http://localhost:3000/api/paylater/eligibility?user_id=${user_id}&cart_id=${cart_id}`
          );
          const data = await res.json();
          setEligibility(data);

          // Create 3 instalments (amount and due dates)
          const perInstalment = parseFloat((foundCart.total_amount / 3).toFixed(2));
          const baseDate = new Date();
          const locale = foundUser.locale || "en-US";
          const timezone = foundUser.timezone || "UTC";

          setInstalments([
  { due_date: baseDate.toISOString(), amount: perInstalment },
  { due_date: new Date(baseDate.getTime() + 30 * 24 * 60 * 60 * 1000).toISOString(), amount: perInstalment },
  { due_date: new Date(baseDate.getTime() + 60 * 24 * 60 * 60 * 1000).toISOString(), amount: perInstalment }
]);

        }

        setLoading(false);
      } catch (err: any) {
        console.error(err);
        setError("Failed to load data");
        setLoading(false);
      }
    }
    fetchData();
  }, [user_id, cart_id]);

  const handleConfirm = async () => {
  if (!cart || !user || !eligibility.eligible) return;
  setIsCreating(true);
  setError("");

  try {
    const res = await fetch(`http://localhost:3000/api/paylater/agreement`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        user_id: user.user_id,
        cart_id: cart.cart_id
      }),
    });

    const data = await res.json();
    console.log("Agreement created:", data);

    navigate(`/paylater/agreement/${data.agreement_id}`);
  } catch (err) {
    console.error(err);
    setError("Error creating agreement");
  } finally {
    setIsCreating(false);
  }
};


  if (loading || !cart || !user)
  return <div style={{ marginTop: 80, textAlign: "center" }}>Loading...</div>;

return (
  <div style={{
    background: "#F6FCFA",
    minHeight: "100vh",
    padding: 28,
    display: "flex",
    justifyContent: "center",
    alignItems: "flex-start"
  }}>
    <div
      style={{
        background: "#fff",
        borderRadius: 22,
        maxWidth: 410,
        margin: "0 auto",
        boxShadow: "0 8px 36px #13444E15",
        padding: 0,
        width: "100%"
      }}
    >
      {/* Header with Back button and title */}
      <div style={{
        display: "flex",
        alignItems: "center",
        padding: "0 0 0 14px",
        background: "#FFF",
        borderRadius: "22px 22px 0 0"
      }}>
        <button
         onClick={() => navigate(-1)}
          style={{
            background: "#F6FCFA",
            border: "none",
            borderRadius: "100%",
            width: 40,
            height: 40,
            boxShadow: "0 1px 8px #13444E19",
            color: "#13444E",
            fontSize: 22,
            fontWeight: 700,
            cursor: "pointer",
            margin: "18px 0 12px 0",
            transition: "background 0.16s"
          }}
          title="Back"
        >←</button>
        <div style={{
          flex: 1,
          textAlign: "center",
          color: "#13444E",
          fontWeight: 700,
          fontSize: 18,
          margin: "18px 16px 12px -40px", // negative left to visually center w/ button
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          gap: 9
        }}>
          <img src="/shopping-cart.png" alt="Cart" style={{ width: 22, height: 22, marginRight: 4 }} />
          Select Plan – PayLater
        </div>
        <div style={{ width: 50 }} />
      </div>

      {/* Payment schedule section */}
      <div style={{
  background: "#F6FCFA",
  minHeight: "100vh",
  padding: 28,
  display: "flex",
  justifyContent: "center"
}}>
  <div style={{
    background: "#fff",
    borderRadius: 22,
    maxWidth: 370,
    minWidth: 300,
    margin: "0 auto",
    boxShadow: "0 8px 36px #13444E15",
    padding: 28
  }}>
    <div style={{ fontWeight: 700, fontSize: 15, color: "#2B2D2F", marginBottom: 15 }}>Payment Schedule :</div>
  {instalments.map((item, index) => (
  <div key={index} style={{
    display: "flex",
    alignItems: "flex-start",
    position: "relative",
    minHeight: 70
  }}>
    {/* Circle + line */}
    <div style={{
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      marginRight: 18,
      height: "100%",
      position: "relative"
    }}>
      <div style={{
        width: 32,
        height: 32,
        borderRadius: "50%",
        border: "2.2px solid #38C87B",
        background: "#fff",
        color: "#38C87B",
        fontWeight: 700,
        fontSize: 15,
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        zIndex: 2
      }}>{String(index + 1).padStart(2, '0')}</div>
      {index < instalments.length - 1 && (
        <div style={{
          position: "absolute",
          top: 32,
          left: "50%",
          marginLeft: -1,
          width: 2,
          height: 38,
          borderLeft: "2px dashed #38C87B99",
          zIndex: 1
        }} />
      )}
    </div>
    {/* Info (Ngày và Số tiền trên cùng dòng) */}
    <div style={{
      flex: 1,
      display: "flex",
      alignItems: "center",
      justifyContent: "space-between",
      paddingTop: 5
    }}>
      <div style={{ fontWeight: 600, fontSize: 15, color: "#1B3C3F" }}>
        {index === 0 ? "Pay Today:" : formatDate(item.due_date)}
      </div>
      <div style={{
        fontWeight: 700,
        color: "#38C87B",
        fontSize: 16,
        minWidth: 70,
        textAlign: "right"
      }}>
        ${item.amount.toFixed(2)}
      </div>
    </div>
  </div>
))}


    {/* Fee and total */}
    <div style={{ marginTop: 13, marginBottom: 3, fontSize: 15 }}>
      <b>Fee = $0 </b>
      <span style={{ color: "#A7A7A7", fontSize: 13, marginLeft: 3, fontWeight: 400 }}>
        Late fee applies for overdue payments.
      </span>
    </div>
    <div style={{ marginTop: 5, fontWeight: 700, fontSize: 16, color: "#1B3C3F" }}>
      Total Payable :
      <span style={{ color: "#38C87B", fontWeight: 900, fontSize: 26, marginLeft: 7 }}>
        ${cart.total_amount}
      </span>
      <span style={{
        color: "#9AC18D",
        fontWeight: 600,
        marginLeft: 5,
        fontSize: 14
      }}>
        ({instalments.length}x${instalments[0].amount.toFixed(2)})
      </span>
    </div>
  </div>
</div>


      {/* Eligibility warning (modern notification card) */}
      {!eligibility.eligible && (
        <div style={{
          background: "linear-gradient(90deg,#F7FDF2 90%,#EDEDED 100%)",
          border: "1px solid #FFC822",
          borderRadius: 12,
          color: "#AD8600",
          fontSize: 15,
          margin: "25px 22px 0 22px",
          padding: "16px 18px 14px 18px",
          display: "flex",
          alignItems: "center",
          gap: 12,
          boxShadow: "0 1px 6px #FFC82222"
        }}>
          <span style={{
            fontSize: 20,
            color: "#FFC822"
          }}>⚠️</span>
          <span>
            <b style={{ color: "#AD8600" }}>Not eligible:</b> {eligibility.reason}
          </span>
        </div>
      )}

      {/* Confirm button */}
      <div style={{ margin: "38px 18px 0 18px" }}>
        <button
          style={{
            width: "100%",
            marginTop: 0,
            background: eligibility.eligible ? "linear-gradient(90deg,#62BE76,#38C87B)" : "#A6CBA4",
            color: "#fff",
            border: "none",
            borderRadius: 18,
            fontWeight: 700,
            fontSize: 20,
            padding: "15px 0",
            cursor: eligibility.eligible ? "pointer" : "not-allowed",
            boxShadow: eligibility.eligible ? "0 2px 15px #38C87B22" : "none",
            transition: "background 0.17s"
          }}
          onClick={handleConfirm}
          disabled={!eligibility.eligible || isCreating}
        >
          {isCreating ? "Processing..." : "Confirm & Start Plan"}
        </button>
        {error && <div style={{
          color: "#EA8E23",
          background: "rgba(255,232,180,0.20)",
          fontWeight: 600,
          fontSize: 15,
          padding: "9px 12px",
          borderRadius: 10,
          margin: "12px 0 0 0",
          textAlign: "center"
        }}>{error}</div>}
      </div>
    </div>
  </div>
);
}
