import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

type Instalment = {
  instalment_number: number;
  due_date: string;
  amount: number;
  status: "PAID" | "UPCOMING" | "FAILED" | "DUE";
};

type Agreement = {
  agreement_id: string;
  user_id: string;
  cart_id: string;
  total_amount: number;
  currency: string;
  schedule: Instalment[];
  status: string;
  created_at?: string;
};

type ActivityLogEntry = {
  agreement_id: string;
  timestamp: string;
  action: string;
  detail?: string;
};

export default function PayLaterInstallmentList() {
  const { agreementId } = useParams();
  const [agreement, setAgreement] = useState<Agreement | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [logs, setLogs] = useState<ActivityLogEntry[]>([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetchAgreement();
    // eslint-disable-next-line
  }, [agreementId]);

  async function fetchAgreement() {
    if (!agreementId) return;
    setLoading(true);
    try {
      // fetch agreement + log
      const res = await fetch(`http://localhost:3000/api/paylater/agreement/${agreementId}`);
      const data: Agreement = await res.json();
      setAgreement(data);

      const logRes = await fetch(`http://localhost:3000/api/paylater/activity-log?agreementId=${agreementId}`);
      const logData: ActivityLogEntry[] = await logRes.json();
      setLogs(logData);
    } catch (err) {
      console.error(err);
      setError("Failed to load agreement or logs");
    } finally {
      setLoading(false);
    }
  }

  function formatDate(str: string) {
    const d = new Date(str);
    if (isNaN(d.getTime())) return "--";
    return d.toLocaleDateString("en-GB", { year: "numeric", month: "2-digit", day: "2-digit" });
  }
  function calcRemaining(schedule: Instalment[]) {
    return schedule.filter(i => i.status !== "PAID").reduce((sum, i) => sum + i.amount, 0);
  }

  const handleFail = async (instalmentNumber: number) => {
    if (!agreement) return;
    try {
      await fetch(`http://localhost:3000/api/paylater/agreement/${agreement.agreement_id}/fail`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ instalmentNumber }),
      });
      fetchAgreement();
    } catch (err) {
      console.error(err);
    }
  };

  const handleRetry = async (instalmentNumber: number) => {
    if (!agreement) return;
    try {
      await fetch(`http://localhost:3000/api/paylater/agreement/${agreement.agreement_id}/retry`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ instalmentNumber }),
      });
      fetchAgreement();
    } catch (err) {
      console.error(err);
    }
  };

  // Change this to your config if needed
  const lateFee = 2;

  if (loading) return <div style={{ marginTop: 80, textAlign: "center" }}>Loading...</div>;
  if (!agreement) return <div style={{ marginTop: 80, textAlign: "center" }}>No agreement found</div>;

  return (
    <div style={{
      background: "#F6FCFA",
      minHeight: "100vh",
      padding: 34,
      display: "flex",
      justifyContent: "center"
    }}>
      <div style={{
        background: "#fff",
        borderRadius: 18,
        boxShadow: "0 4px 24px #13444E15",
        maxWidth: 400,
        width: "100%",
        margin: "0 auto",
        padding: "28px 22px 22px 22px"
      }}>
     {/* --- Header thanh toán (cực kỳ tối giản) --- */}
<div style={{
  borderBottom: "1px solid #EFF0F3",
  paddingBottom: 15,
  marginBottom: 18
}}>
  {/* Header with Back button and title */}
     <div style={{
  background: "#fff",
  borderRadius: 22,
  maxWidth: 370,
  minWidth: 300,
  margin: "0 auto",
  boxShadow: "0 8px 36px #13444E15",
  padding: 28,
  marginBottom: 24 // thêm margin-b dưới cho các section sau không đè sát
}}>
      }}>
       <button
          onClick={() => navigate(-1)}
          style={{
            background: "transparent",
            border: "none",
            borderRadius: "50%",
            width: 40,
            height: 40,
            color: "#13444E",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            marginRight: 13,
            padding: 0,
            cursor: "pointer"
          }}
          aria-label="Back"
        >
          {/* iOS Back Icon SVG */}
          <svg width="24" height="24" fill="none" stroke="#13444E" strokeWidth="3" strokeLinecap="round" strokeLinejoin="round" viewBox="0 0 24 24" style={{ display: "block" }}>
            <path d="M15 18l-6-6 6-6"></path>
          </svg>
        </button>

        <div style={{
          flex: 1,
          textAlign: "center",
          color: "#13444E",
          fontWeight: 700,
          fontSize: 18,
          margin: "18px 16px 12px -40px", // negative left to visually center w/ button
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          gap: 9
        }}>
          <img src="/shopping-cart.png" alt="Cart" style={{ width: 22, height: 22, marginRight: 4 }} />
          Select Plan – PayLater
        </div>
        <div style={{ width: 50 }} />
      </div>

  
  {/* Tiêu đề và Plan ID badge */}
  <div style={{
    fontWeight: 700,
    fontSize: 16,
    color: "#13444E",
    marginBottom: 9,
    letterSpacing: 0.2,
    display: "flex",
    alignItems: "center"
  }}>
    PayLater Plan
    <span style={{
      background: "#EAF3EE",
      color: "#38C87B",
      borderRadius: 6,
      padding: "2px 10px",
      fontWeight: 700,
      marginLeft: 10,
      fontSize: 13.5
    }}>
      #{agreement.agreement_id?.slice(0, 8).toUpperCase()}
    </span>
  </div>
  <div style={{
    display: "flex", flexDirection: "column", gap: 6
  }}>
    <div style={{
      display: "flex", justifyContent: "space-between", fontSize: 14.5, color: "#353C45", fontWeight: 500
    }}>
      <span>Transaction Date</span>
      <span>{agreement.created_at ? formatDate(agreement.created_at) : "--"}</span>
    </div>
    <div style={{
      display: "flex", justifyContent: "space-between", fontSize: 15, fontWeight: 500, color: "#353C45"
    }}>
      <span>Total Amount</span>
      <span>${agreement.total_amount.toFixed(2)}</span>
    </div>
    <div style={{
      display: "flex", justifyContent: "space-between", fontSize: 14.5, color: "#353C45", fontWeight: 500
    }}>
      <span>Remaining Balance</span>
      <span>${calcRemaining(agreement.schedule).toFixed(2)}</span>
    </div>
    <div style={{
      display: "flex", justifyContent: "space-between", fontSize: 14.2, fontWeight: 500
    }}>
      <span>Late Fee</span>
      <span style={{ color: "#E7A12A", fontWeight: 600 }}>
        ${lateFee ?? "0"}
      </span>
    </div>
  </div>
</div>


        {agreement.schedule.map((inst, idx) => {
  // ... status styling như cũ
  let circle, color, statusLabel;
  if (inst.status === "PAID") {
    circle = (
      <span style={{
        width: 20, height: 20, borderRadius: "50%", background: "#38C87B",
        color: "#fff", display: "flex", alignItems: "center", justifyContent: "center", fontSize: 13
      }}>✓</span>
    );
    color = "#38C87B";
    statusLabel = "PAID";
  } else if (inst.status === "FAILED") {
    circle = (
      <span style={{
        width: 20, height: 20, borderRadius: "50%", background: "#D32F2F",
        color: "#fff", display: "flex", alignItems: "center", justifyContent: "center", fontSize: 14
      }}>×</span>
    );
    color = "#D32F2F";
    statusLabel = "FAILED";
  } else {
    circle = (
      <span style={{
        width: 20, height: 20, borderRadius: "50%", border: "2px solid #E0A612",
        color: "#E0A612", background: "#F8F6EF", display: "flex", alignItems: "center", justifyContent: "center", fontWeight: 700, fontSize: 13
      }}>{idx + 1}</span>
    );
    color = "#E0A612";
    statusLabel = inst.status;
  }

  /** Sửa: chứa icon và line trong một div cao, position:relative */
  return (
    <div key={inst.instalment_number} style={{
      display: "flex",
      alignItems: "center",
      marginBottom: 13,
      minHeight: 55
    }}>
      <div style={{ width: 26, minWidth: 26, display: "flex", flexDirection: "column", alignItems: "center", position: "relative" }}>
        {/* Circle */}
        <div style={{ zIndex: 2 }}>{circle}</div>
        {/* Dotted line dưới icon, không che icon */}
        {idx < agreement.schedule.length - 1 && (
          <div style={{
            position: "absolute",
            top: 20, // đúng bằng chiều cao của icon circle
            left: "50%",
            marginLeft: -1, // canh giữa
            width: 0,
            height: 33,
            borderLeft: "2px dotted #EDE8CF",
            zIndex: 1,
          }} />
        )}
      </div>
      {/* Info card */}
      <div style={{
        flex: 1, marginLeft: 10,
        boxShadow: "0 1.5px 6px #EDE3BF11",
        borderRadius: 10,
        background: "#F8F7F3",
        padding: "9px 15px",
        display: "flex",
        flexDirection: "column"
      }}>
        <div style={{
          fontSize: 13.5,
          color: color,
          fontWeight: inst.status === "PAID" ? 600 : 700,
          letterSpacing: 0.1,
          marginBottom: 1
        }}>
          {`${inst.instalment_number}/${agreement.schedule.length} : ${formatDate(inst.due_date)} `}
          <span style={{ textTransform: "uppercase", fontWeight: 700 }}>({statusLabel})</span>
        </div>
        <div style={{
          background: "#fff",
          border: "1px solid #F3F3F3",
          borderRadius: 7,
          fontWeight: 700,
          fontSize: 14.2,
          color: "#3A4D3D",
          padding: "7px 12px",
          marginTop: 4,
          boxShadow: "0 0.5px 3px #dde8d9",
          display: "flex",
          alignItems: "center"
        }}>
          Amount Due : ${inst.amount.toFixed(2)}
          {/* Action buttons */}
          {["UPCOMING", "DUE"].includes(inst.status) && (
            <button
              onClick={() => handleFail(inst.instalment_number)}
              style={{
                marginLeft: 16,
                padding: "2px 12px",
                background: "#D32F2F",
                color: "#fff",
                border: "none",
                borderRadius: 5,
                fontSize: 13,
                fontWeight: 600,
                cursor: "pointer"
              }}
            >
              Fail
            </button>
          )}
          {["FAILED", "DUE"].includes(inst.status) && (
            <button
              onClick={() => handleRetry(inst.instalment_number)}
              style={{
                marginLeft: 12,
                padding: "2px 12px",
                background: "#1976D2",
                color: "#fff",
                border: "none",
                borderRadius: 5,
                fontSize: 13,
                fontWeight: 600,
                cursor: "pointer"
              }}
            >
              Retry
            </button>
          )}
        </div>
      </div>
    </div>
  );
})}


        

        {/* Nếu có lỗi hoặc log dev */}
        {error && <div style={{ color: "#D32F2F", textAlign: "center", marginTop: 10 }}>{error}</div>}
        {logs.length > 0 && (
          <div style={{ marginTop: 14, fontSize: 12, color: "#555" }}>
            <h4>Activity Log (Developer)</h4>
            {logs.map((log, idx) => (
              <div key={idx}>
                [{new Date(log.timestamp).toLocaleString()}] {log.action} {log.detail ? `- ${log.detail}` : ""}
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
