import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

type Cart = {
  cart_id: string;
  total_amount: number;
  eligible_threshold: number;
  item_count: number;
};

type User = {
  user_id: string;
  name: string;
  timezone: string;
  locale: string;
};

type Instalment = {
  due_date: string;
  amount: number;
};

export default function PaylaterDetailScreen() {
  const { user_id, cart_id } = useParams();
  const navigate = useNavigate();

  const [user, setUser] = useState<User | null>(null);
  const [cart, setCart] = useState<Cart | null>(null);
  const [instalments, setInstalments] = useState<Instalment[]>([]);
  const [eligibility, setEligibility] = useState<{ eligible: boolean; reason?: string }>({ eligible: false });
  const [loading, setLoading] = useState(true);
  const [isCreating, setIsCreating] = useState(false);
  const [error, setError] = useState("");

  useEffect(() => {
    async function fetchData() {
      try {
        // Fetch user
        const usersRes = await fetch(`http://localhost:3000/api/paylater/users`);
        const users: User[] = await usersRes.json();
        const foundUser = users.find(u => u.user_id === user_id);
        setUser(foundUser || null);

        // Fetch cart
        const cartsRes = await fetch(`http://localhost:3000/api/paylater/users/${user_id}/carts`);
        const carts: Cart[] = await cartsRes.json();
        const foundCart = carts.find(c => c.cart_id === cart_id);
        setCart(foundCart || null);

        if (foundUser && foundCart) {
          // Check eligibility via API
          const res = await fetch(
            `http://localhost:3000/api/paylater/eligibility?user_id=${user_id}&cart_id=${cart_id}`
          );
          const data = await res.json();
          setEligibility(data);

          // Create 3 instalments (amount and due dates)
          const perInstalment = parseFloat((foundCart.total_amount / 3).toFixed(2));
          const baseDate = new Date();
          const locale = foundUser.locale || "en-US";
          const timezone = foundUser.timezone || "UTC";

          setInstalments([
            {
              due_date: baseDate.toLocaleDateString(locale, { timeZone: timezone, year: "numeric", month: "short", day: "numeric" }),
              amount: perInstalment
            },
            {
              due_date: new Date(baseDate.getTime() + 30 * 24 * 60 * 60 * 1000).toLocaleDateString(locale, { timeZone: timezone, year: "numeric", month: "short", day: "numeric" }),
              amount: perInstalment
            },
            {
              due_date: new Date(baseDate.getTime() + 60 * 24 * 60 * 60 * 1000).toLocaleDateString(locale, { timeZone: timezone, year: "numeric", month: "short", day: "numeric" }),
              amount: perInstalment
            }
          ]);
        }

        setLoading(false);
      } catch (err: any) {
        console.error(err);
        setError("Failed to load data");
        setLoading(false);
      }
    }
    fetchData();
  }, [user_id, cart_id]);

  const handleConfirm = async () => {
  if (!cart || !user || !eligibility.eligible) return;
  setIsCreating(true);
  setError("");

  try {
    const res = await fetch(`http://localhost:3000/api/paylater/agreement`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        user_id: user.user_id,
        cart_id: cart.cart_id
      }),
    });

    const data = await res.json();
    console.log("Agreement created:", data);

    navigate(`/paylater/agreement/${data.agreement_id}`);
  } catch (err) {
    console.error(err);
    setError("Error creating agreement");
  } finally {
    setIsCreating(false);
  }
};


  if (loading || !cart || !user)
    return <div style={{ marginTop: 80, textAlign: "center" }}>Loading...</div>;

  return (
    <div style={{ background: "#F6FCFA", minHeight: "100vh", padding: 32 }}>
      <div
        style={{
          background: "#fff",
          borderRadius: 26,
          maxWidth: 420,
          margin: "0 auto",
          boxShadow: "0 2px 12px #38C87B22",
          padding: 28
        }}
      >
        <h2 style={{ color: "#13444E", marginBottom: 18 }}>Select Plan â€“ PayLater 3 Instalments</h2>

        <div
          style={{
            background: "#F6FCFA",
            borderRadius: 14,
            padding: 19,
            marginBottom: 26,
            fontSize: 15
          }}
        >
          {instalments.map((item, i) => (
            <div
              key={i}
              style={{ display: "flex", alignItems: "center", marginBottom: i < 2 ? 16 : 0 }}
            >
              <span
                style={{
                  fontWeight: 700,
                  fontSize: 17,
                  color: "#1B3C3F",
                  width: 50
                }}
              >
                {i === 0 ? "Today:" : `In ${i * 30}d:`}
              </span>
              <span
                style={{
                  flex: 1,
                  marginLeft: 10,
                  color: "#4B655C",
                  fontWeight: 600
                }}
              >
                {item.due_date}
              </span>
              <span
                style={{
                  fontWeight: 700,
                  color: "#38C87B",
                  fontSize: 18,
                  marginLeft: 12
                }}
              >
                ${item.amount.toFixed(2)}
              </span>
            </div>
          ))}
          <div style={{ marginTop: 15, fontSize: 14, color: "#4B655C", fontWeight: 600 }}>
            Total:{" "}
            <span style={{ color: "#38C87B", fontWeight: 700, fontSize: 16 }}>
              ${cart.total_amount.toFixed(2)}
            </span>
          </div>
        </div>

        {!eligibility.eligible && (
          <div style={{ color: "red", marginBottom: 12 }}>{eligibility.reason}</div>
        )}

        <div style={{ margin: "16px 0 5px 0", display: "flex", alignItems: "center", gap: 13 }}>
          <input type="checkbox" checked readOnly style={{ accentColor: "#38C87B", width: 20, height: 20 }} />
          <span style={{ color: "#3D4644", fontSize: 15, fontWeight: 500 }}>
            I agree to pay in 3 instalments as shown above
          </span>
        </div>

        <button
          style={{
            width: "100%",
            marginTop: 26,
            background: "#38C87B",
            color: "#fff",
            border: "none",
            borderRadius: 15,
            fontWeight: 700,
            fontSize: 20,
            padding: "15px 0"
          }}
          onClick={handleConfirm}
          disabled={!eligibility.eligible || isCreating}
        >
          {isCreating ? "Processing..." : "Confirm & Start Plan"}
        </button>
        {error && <div style={{ color: "#B7E82A", textAlign: "center", marginTop: 11 }}>{error}</div>}
      </div>
    </div>
  );
}
